inicio:
/* Nivel 4: Cambiá la caja violeta a rosa, centrá el texto y aumentá el tamaño de la letra */
.box {
    width: 30vw;
    height: 30vh;
    background-color: purple;
    display: grid;
    place-items: end;
    color: blue;
    font-size: 5rem;
}

final:
.box2 {
    width: 30vw;
    height: 30vh;
    background-color: pink;
    display: grid;
    place-items: center;
    color: white;
    font-size: 2rem;
}

inicio:
/* Nivel 5: Formá la bandera de Francia usando display grid, cambiando el tamaño de las columnas y los colores */
.container {
    width: 80vw;
    height: 50vh;
    display: block;
    grid-template-columns: 1fr 10vh 1fr;
    gap: 2vh;
}
.item1{
    background-color: pink;
}
.item2{
    background-color: green;
}
.item3{
    background-color: blue;
}

final:
.container2 {
    width: 80vw;
    height: 50vh;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 0vh;
}
.item11{
    background-color: blue;
}
.item22{
    background-color: white;
}
.item33{
    background-color: red;
}

inicio:
/* Nivel 6: Formá la bandera de Argentina usando display grid, cambiando el tamaño de las columnas, los colores y centrando el sol */
.container {
    width: 80vw;
    height: 50vh;
    display: flex;
    grid-template-columns: 1fr 10vh 1fr;
    gap: 0vh;
}
.item1{
    background-color: pink;
    align-items: start;
    justify-content: center;
}
.item2{
    background-color: cyan;
}
.item3{
    background-color: yellow;
    display: flex;
}
.sol{
    background-color: red;
    border-radius: 20%;
    width: 2vh;
    height: 20vh;
}

final:
.container2 {
    width: 80vw;
    height: 50vh;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 1vh;
}
.item11{
    background-color: cyan;
}
.item22{
    background-color: white;
    display: flex;
    align-items: center;
    justify-content: center;
}
.item33{
    background-color: cyan;
}
.sol2{
