}

inicio:
/* Nivel 7: Creá un footer gris y después cambialo a negro, con texto blanco alineado a los extremos */
.footer {
    width: 73%;
    height: 5vh;
    background-color: gray;
    display: block;
    justify-content: end;
    align-items: space-between;
    color: red;
    padding: 3vh 0vh;
}

final:
.footer2 {
    width: 100%;
    height: 5vh;
    background-color: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: white;
    padding: 0vh 1vh;
}

inicio:
/* Nivel 8: Hacé una tarjeta beige con margen, después cambiá a lightcoral y organizá el contenido arriba y abajo con flex */
.card {
    width: 25vw;
    height: 35vh;
    background-color: beige;
    margin: 5px;
    display: block;
    flex-direction: row;
    justify-content: start;
    align-items: flex-start;
    padding: 5px;
}

final:
.card2 {
    width: 20vw;
    height: 30vh;
    background-color: lightcoral;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
}

inicio:
/* Nivel 9: Círculo chico amarillo, después aumentá su tamaño a grande y cambiá a naranja, posicionándolo con top y left */
.circle-small {
    width: 25px;
    height: 25px;
    border-radius: 25%;
    background-color: yellow;
    position: absolute;
    top: 5%;
    left: 5%;
    transform: rotate(15deg);
}

final:
.circle-large {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    background-color: orange;
    position: absolute;
    top: 20%;
    left: 30%;
}

inicio:
/* Nivel 10: Hacé un grid container lavanda sin columnas ni filas, después agregá 4 columnas y 3 filas con gap y color plum */
.grid-container {
    display: block;
    width: 85vw;
    height: 65vh;
    background-color: lavender;
    gap: 0px;
    grid-template-columns: 2fr 1fr;
    grid-template-rows: auto;
    padding: 5px;
}

final:
.grid-container2 {
    display: grid;
    width: 80vw;
    height: 60vh;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(3, 1fr);
    gap: 10px;
    background-color: plum;
}

inicio:
