inicio:
/* Nivel 13: Crea un cuadrado naranja, luego aumentá tamaño y rotalo */
.square {
    width: 10vh;
    height: 10vh;
    background-color: red;
    transform: rotate(45deg);
}
.square:hover {
    transform: rotate(0deg) scale(1);
}

final:
.square2 {
    width: 20vh;
    height: 20vh;
    background-color: orange;
    transform: rotate(0deg);
}
.square2:hover {
    transform: rotate(45deg) scale(2);
}

inicio:
/* Nivel 14: Crea un círculo rojo y hacelo crecer al pasar el mouse */
.circle {
    width: 10vh;
    height: 10vh;
    background-color: darkgreen;
    border-radius: 50%;
    transition: all 0.3s ease;
}
.circle:hover {
    transform: scale(1);
}

final:
.circle2 {
    width: 15vh;
    height: 15vh;
    background-color: darkred;
    border-radius: 50%;
    transition: all 0.3s ease;
}
.circle2:hover {
    transform: scale(1.5);
}

inicio:
/* Nivel 15: Crea un rectángulo azul y hacelo girar en hover */
.rectangle {
    width: 20vw;
    height: 8vh;
    background-color: lightblue;
    transition: transform 0.5s;
    opacity: 100%;
}
.rectangle:hover {
    transform: rotate(0deg);
    opacity: 10%;
    background-color: red;
}

final:
.rectangle2 {
    width: 25vw;
    height: 10vh;
    background-color: blue;opacity: 50%;
    transition: transform 0.5s;
}
.rectangle2:hover {
    transform: rotate(180deg);
    opacity: 100%;
    background-color: lightblue;
}
