inicio:
/* Nivel 1: Cambiá el color del cubo rojo a azul y movelo al centro */
.cube {
    width: 10vh;
    height: 10vh;
    background-color: red;
    position: absolute;
    top: 0%;
    left: 0%;
}

final:
.cube2 {
    width: 10vh;
    height: 10vh;
    background-color: blue;
    position: absolute;
    top: 50%;
    left: 50%;
}

inicio:
/* Nivel 2: Hacé un círculo naranja, después aumentá su tamaño a 20vh y cambialo a verde, redondeando los bordes */
.circle {
    width: 10vh;
    height: 10vh;
    background-color: orange;
    border-radius: 0%;
    position: absolute;
    top: 0%;
    left: 0%;
}

final:
.circle2 {
    width: 20vh;
    height: 20vh;
    background-color: green;
    border-radius: 50%;
    position: absolute;
    top: 20%;
    left: 40%;
}

inicio:
/* Nivel 3: Cambiá el header a negro, la fuente a blanco, hacelo ancho para cubrir toda la pantalla y centrá el texto */
header {
    width: 50%;
    height: 10vh;
    background-color: white;
    color: black;
    display: flex;
    justify-content: center;
    align-items: end;
}

final:
header.final {
    width: 100%;
    height: 10vh;
    background-color: black;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white;
}

inicio:
/* Nivel 4: Cambiá la caja violeta a rosa, centrá el texto y aumentá el tamaño de la letra */
.box {
    width: 30vw;
    height: 30vh;
    background-color: purple;
    display: grid;
    place-items: end;
    color: blue;
    font-size: 5rem;
}

final:
.box2 {
    width: 30vw;
    height: 30vh;
    background-color: pink;
    display: grid;
    place-items: center;
    color: white;
    font-size: 2rem;
}

inicio:
/* Nivel 5: Formá la bandera de Francia usando display grid, cambiando el tamaño de las columnas y los colores */
.container {
    width: 80vw;
    height: 50vh;
    display: block;
    grid-template-columns: 1fr 10vh 1fr;
    gap: 2vh;
}
.item1{
    background-color: pink;
}
.item2{
    background-color: green;
}
.item3{
    background-color: blue;
}

final:
.container2 {
    width: 80vw;
    height: 50vh;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 0vh;
}
.item11{
    background-color: blue;
}
.item22{
    background-color: white;
}
.item33{
    background-color: red;
}

inicio:
/* Nivel 6: Formá la bandera de Argentina usando display grid, cambiando el tamaño de las columnas, los colores y centrando el sol */
.container {
    width: 80vw;
    height: 50vh;
    display: flex;
    grid-template-columns: 1fr 10vh 1fr;
    gap: 0vh;
}
.item1{
    background-color: pink;
    align-items: start;
    justify-content: center;
}
.item2{
    background-color: cyan;
}
.item3{
    background-color: yellow;
    display: flex;
}
.sol{
    background-color: red;
    border-radius: 20%;
    width: 2vh;
    height: 20vh;
}

final:
.container2 {
    width: 80vw;
    height: 50vh;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 1vh;
}
.item11{
    background-color: cyan;
}
.item22{
    background-color: white;
    display: flex;
    align-items: center;
    justify-content: center;
}
.item33{
    background-color: cyan;
}
.sol2{
    background-color: yellow;
    border-radius: 50%;
    width: 10vh;
    height: 10vh;
}

inicio:
/* Nivel 7: Creá un footer gris y después cambialo a negro, con texto blanco alineado a los extremos */
.footer {
    width: 100vw;
    height: 5vh;
    background-color: gray;
    display: block;
}

final:
.footer2 {
    width: 100vw;
    height: 5vh;
    background-color: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: white;
    padding: 0 20px;
}

inicio:
/* Nivel 8: Hacé una tarjeta beige con margen, después cambiá a lightcoral y organizá el contenido arriba y abajo con flex */
.card {
    width: 20vw;
    height: 30vh;
    background-color: beige;
    margin: 10px;
    display: block;
}

final:
.card2 {
    width: 20vw;
    height: 30vh;
    background-color: lightcoral;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
}

inicio:
/* Nivel 9: Círculo chico amarillo, después aumentá su tamaño a grande y cambiá a naranja, posicionándolo con top y left */
.circle-small {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background-color: yellow;
    position: absolute;
    top: 0;
    left: 0;
}

final:
.circle-large {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    background-color: orange;
    position: absolute;
    top: 20%;
    left: 30%;
}

inicio:
/* Nivel 10: Hacé un grid container lavanda sin columnas ni filas, después agregá 4 columnas y 3 filas con gap y color plum */
.grid-container {
    display: grid;
    width: 80vw;
    height: 60vh;
    background-color: lavender;
}

final:
.grid-container2 {
    display: grid;
    width: 80vw;
    height: 60vh;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(3, 1fr);
    gap: 10px;
    background-color: plum;
}

inicio:
/* Nivel 11: Hacé una hero section lightgreen, después cambiá a seagreen, centrando el texto y aumentando el tamaño de la letra */
.hero {
    width: 100vw;
    height: 50vh;
    background-color: lightgreen;
    display: block;
}

final:
.hero2 {
    width: 100vw;
    height: 50vh;
    background-color: seagreen;
    display: flex;
    justify-content: center;
    align-items: center;
    color: white;
    font-size: 2rem;
}
